mapper.select:
select * from cheat 
where user_id=#{userId} 

and (title like '%'||#{form.q}||'%' or code like '%'||#{form.q}||'%' or detail like '%'||#{form.q}||'%') 


and (tag_id=#{form.tagId}) 

order by id desc limit #{pageable.pageSize} 
offset #{pageable.offset}

select count(*) from cheat where user_id=#{userId}
and (title like '%'||#{form.q}||'%' or code like '%'||#{form.q}||'%' or detail like '%'||#{form.q}||'%')
and (tag_id=#{form.tagId})
